import React, { useState, useEffect } from 'react';

const phrases = [
  'user interfaces',
  'user experiences',
  'website design',
];

function Typewriter({ words, speed = 150, pause = 2000 }) {
  const [displayedText, setDisplayedText] = useState('');
  const [wordIndex, setWordIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [deleting, setDeleting] = useState(false);

  useEffect(() => {
    let timeout;
    const currentWord = words[wordIndex];

    if (!deleting) {
      if (charIndex < currentWord.length) {
        // Typing forward
        timeout = setTimeout(() => {
          setDisplayedText(currentWord.substring(0, charIndex + 1));
          setCharIndex(charIndex + 1);
        }, speed);
      } else {
        // Word fully typed, pause before deleting
        timeout = setTimeout(() => {
          setDeleting(true);
        }, pause);
      }
    } else {
      if (charIndex > 0) {
        // Deleting backward
        timeout = setTimeout(() => {
          setDisplayedText(currentWord.substring(0, charIndex - 1));
          setCharIndex(charIndex - 1);
        }, speed / 2);
      } else {
        // Finished deleting, move to next word
        timeout = setTimeout(() => {
          setDeleting(false);
          setWordIndex((wordIndex + 1) % words.length);
        }, 500);
      }
    }

    return () => clearTimeout(timeout);
  }, [charIndex, deleting, wordIndex, words, speed, pause]);

  return <span>{displayedText}</span>;
}

function Tagline() {
  return (
    <div>
      <h1 className="tagline">
        an interdisciplinary designer
        <br />
        focused on creating
        <br />
        <span className="typewriter-container">
        meaningful <Typewriter words={phrases} />
        </span>
      </h1>
    </div>
  );
}

function HumorProjectTextSection() {
  return (
    <div style={{ maxWidth: "50%", margin: "0 auto" }}>
      <h1>The Humor Project</h1>
      <h2>second mini header describing the project</h2>
      <p>description description description description</p>
    </div>
  );
}


