import React, { useState, useEffect } from 'react';
import "../styles/Home.css";
import crackd1 from "../assets/crackd-homepage.png";
import crackd2 from "../assets/crackdmultihumor.png";
import crackd3 from "../assets/crackdLogoBlack.png";
import nyu from "../assets/nyu.png";
import alruwais from "../assets/alruwais-mockup.png";
import consumerreports from "../assets/CR.png";

const phrases = [
  'user interfaces',
  'user experiences',
  'website design',
];

function Typewriter({ words, speed = 150, pause = 2000 }) {
  const [displayedText, setDisplayedText] = useState('');
  const [wordIndex, setWordIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [deleting, setDeleting] = useState(false);

  useEffect(() => {
    let timeout;
    const currentWord = words[wordIndex];

    if (!deleting) {
      if (charIndex < currentWord.length) {
        // Typing forward
        timeout = setTimeout(() => {
          setDisplayedText(currentWord.substring(0, charIndex + 1));
          setCharIndex(charIndex + 1);
        }, speed);
      } else {
        // Word fully typed, pause before deleting
        timeout = setTimeout(() => {
          setDeleting(true);
        }, pause);
      }
    } else {
      if (charIndex > 0) {
        // Deleting backward
        timeout = setTimeout(() => {
          setDisplayedText(currentWord.substring(0, charIndex - 1));
          setCharIndex(charIndex - 1);
        }, speed / 2);
      } else {
        // Finished deleting, move to next word
        timeout = setTimeout(() => {
          setDeleting(false);
          setWordIndex((wordIndex + 1) % words.length);
        }, 500);
      }
    }

    return () => clearTimeout(timeout);
  }, [charIndex, deleting, wordIndex, words, speed, pause]);

  return <span>{displayedText}</span>;
}

function Tagline() {
  return (
    <div>
      <h1 className="tagline">
        an interdisciplinary designer
        <br />
        focused on creating
        <br />
        <span className="typewriter-container">
